/**
 * Get defaults for our general options.
 */
function olympus_blocks_get_option_defaults() {
	return apply_filters(
		'olympus_blocks_option_defaults',
		array(
			'load_css_file' => true,
			'load_fonts_locally' => true,
			'preload_local_fonts' => true,
			'sync_responsive_previews' => true,
		)
	);
}

/**
 * Styles to use in the editor/font-end when needed.
 */
function olympus_blocks_get_default_styles() {
	$defaults = olympus_blocks_get_block_defaults();

	$defaultBlockStyles = array(
		'button' => array(
			'backgroundColor' => $defaults['button']['backgroundColor'] ? $defaults['button']['backgroundColor'] : '#ff9d52',
			'textColor' => $defaults['button']['textColor'] ? $defaults['button']['textColor'] : '#ffffff',
			'backgroundColorHover' => $defaults['button']['backgroundColorHover'] ? $defaults['button']['backgroundColorHover'] : '#ffb175',
			'textColorHover' => $defaults['button']['textColorHover'] ? $defaults['button']['textColorHover'] : '#ffffff',
			'paddingTop' => $defaults['button']['paddingTop'] ? $defaults['button']['paddingTop'] : '12',
			'paddingRight' => $defaults['button']['paddingRight'] ? $defaults['button']['paddingRight'] : '24',
			'paddingBottom' => $defaults['button']['paddingBottom'] ? $defaults['button']['paddingBottom'] : '12',
			'paddingLeft' => $defaults['button']['paddingLeft'] ? $defaults['button']['paddingLeft'] : '24',
		),
		'section' => array(
			'paddingTop' => '10',
			'paddingRight' => '10',
			'paddingBottom' => '10',
			'paddingLeft' => '10',
			'bgImageSize' => 'full',
			'shapeDividers' => array(
				'shape' => 'ob-mountains',
